import { Component, Inject } from '@angular/core';
import { AuthService } from '../auth-service/auth-service';
import { HttpClient } from '@angular/common/http';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
})
export class LoginComponent {
  public login: string = '';
  public password: string = '';
  private _baseUrl: string = '';
  private _http: HttpClient;
  private _authService: AuthService;
  constructor(private authService: AuthService, private http: HttpClient, @Inject('BASE_URL') baseUrl: string) {
    this._baseUrl = baseUrl;
    this._http = http;
    this._authService = authService;
  }

  onSubmit() {
    const loginUrl = `${this._baseUrl}urlservice/Authorize/${this.login}/${this.password}`;
    this._http.get(loginUrl).subscribe(result => {
      const parsedResult = result as { roleName: string };
      alert(`Welcome,${parsedResult.roleName}`);
      this._authService.setRole(parsedResult.roleName); 
    },
      error => {
        error = error as { message: string };
        alert(error.message)
      }
    );
  }
}
<h2>Авторизация</h2>
<form (ngSubmit)="onSubmit()">
  <div>
    <label for="login">Логин:</label>
    <input type="text" id="login" name="login" [(ngModel)]="login" required>
  </div>
  <div>
    <label for="password">Пароль:</label>
    <input type="password" id="password" name="password" [(ngModel)]="password" required>
  </div>
  <button type="submit">Войти</button>
</form>
