import { Injectable } from '@angular/core';

@Injectable({
  providedIn: 'root'
})
export class AuthService{
  private role: string = 'Unauthorized';

  setRole(role: string) {
    this.role = role;
  }

  isAdmin(): boolean {
    return this.role == "Admin";
  }
  isOrdinary(): boolean {
    return this.role == "Ordinary";
  }

  getRole(): string {
    return this.role;
  }
}
