import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
@Injectable({
  providedIn: 'root'
})
export class AuthService{
  /*private role: string = 'Unauthorized';*/
  private roleSubject = new BehaviorSubject<string>('Unauthorized'); // Измените тип на ваш тип роли
  private role$ = this.roleSubject.asObservable();
  public login: string = '';

  setRole(role: string) {
    this.roleSubject.next(role);
  }
  getRole(): string {
    return this.roleSubject.getValue();
  }
  isAdmin(): boolean {
    return this.roleSubject.getValue() == "Admin";
  }
  isOrdinary(): boolean {
    return this.roleSubject.getValue() == "Ordinary";
  }

}
